UPDATE `tbl_cfg_message_template` SET `template` = '{\"title\": \"Your Order Has Shipped\", \"content\": \"ext\", \"type\": \"html\"}', `ext` = '<html><body><p style=\"font-family: Arial;\"><img src={logo_url} style=\"height: 30px\"></img></p><hr /><div class=\"content\"><p style=\"font-size: 10px; font-family: Arial;\">Hello,</p><p style=\"font-size: 10px; font-family: Arial;\">A shipment from order(<u>{order_id}</u>) is on the way.</p><p style=\"font-size: 10px; font-family: Arial;\">{shipper_name}</p><p style=\"font-size: 10px; font-family: Arial;\">Tracking Number: {tracking_no}</p><p style=\"font-size: 10px; font-family: Arial;\">Thanks for visiting <a href=\"{shop_home_page}\"  style=\"font-weight: bold; color: #00A2F2;\">{shop_name}</a></p></div></body></html>', `status` = 1, `create_time` = '2017-07-05 16:19:27' WHERE `message_id` = 1 AND `shop_id` = '-1' AND `admin_id` = 0 AND `media_type` = 3 AND `sale_channel` = 1;
UPDATE `tbl_cfg_message_template` SET `template` = '{\"title\": \"Your {shop_name} sign-up verification code\", \"content\": \"ext\", \"type\": \"html\"}', `ext` = '<html><body><p style=\"font-family: Arial;\"><img src={logo_url} style=\"height: 30px\"></img></p><hr /><div class=\"content\"><p style=\"font-size: 10px; font-family: Arial;\">Hello,</p><p style=\"font-size: 10px; font-family: Arial;\">Please enter the following verification code to complete your sign-up.</p><p><span style=\"font-weight: bold; font-size: 13px; font-family: Arial;\">{verify_code}</span></p><p style=\"font-size: 10px; font-family: Arial;\">Thanks for visiting <a href=\"{shop_home_page}\"  style=\"font-weight: bold; color: #00A2F2;\">{shop_name}</a></p></div></body></html>', `status` = 1, `create_time` = '2017-09-18 17:14:45' WHERE `message_id` = 7 AND `shop_id` = '-1' AND `admin_id` = 0 AND `media_type` = 3 AND `sale_channel` = 1;
INSERT INTO `tbl_cfg_message_template`(`message_id`, `shop_id`, `admin_id`, `media_type`, `sale_channel`, `template`, `ext`, `status`, `create_time`) VALUES (8, '-1', 0, 3, 1, '{\"title\": \"Reset your password\", \"content\": \"ext\", \"type\": \"html\"}', '<html><body><p style=\"font-family: Arial;\"><img src={logo_url} style=\"height: 30px\"></img></p><hr /><div class=\"content\"><p style=\"font-size: 10px; font-family: Arial;\">Hello,</p><p style=\"font-size: 10px; font-family: Arial;\">Please enter the following verification code to complete your password reset.</p><p><span style=\"font-weight: bold; font-size: 13px; font-family: Arial;\">{verify_code}</span></p><p style=\"font-size: 10px; font-family: Arial;\">Thanks for visiting <a href=\"{shop_home_page}\"  style=\"font-weight: bold; color: #00A2F2;\">{shop_name}</a></p></div></body></html>', 1, '2017-09-18 17:14:45');